mcScreen.Event:
local inst = mc.mcGetInstance() local rc = 0; testcount = testcount + 1 machState, rc = mc.mcCntlGetState(inst); local inCycle = mc.mcCntlIsInCycle(inst);
tooldesc()
------------------------------------------------------- -- Set plate align (G68) Led ------------------------------------------------------- local curLedState = math.tointeger(scr.GetProperty("ledPlateAlign", "Value")) local curAlignState = math.tointeger((mc.mcCntlGetPoundVar(inst, 4016) - 69)) curAlignState = math.abs(curAlignState) if (curLedState ~= curAlignState) then scr.SetProperty("ledPlateAlign", "Value", tostring(curAlignState)) end ------------------------------------------------------- -- Coroutine resume ------------------------------------------------------- if (wait ~= nil) and (machState == 0) then --wait exist and state == idle local state = coroutine.status(wait) if state == "suspended" then --wait is suspended coroutine.resume(wait) end end ------------------------------------------------------- -- Cycle time label update ------------------------------------------------------- --Requires a static text box named "CycleTime" on the screen if (machEnabled == 1) then local cycletime = mc.mcCntlGetRunTime(inst, time) scr.SetProperty("CycleTime", "Label", SecondsToTime(cycletime)) end ------------------------------------------------------- -- Set Height Offset Led ------------------------------------------------------- local HOState = mc.mcCntlGetPoundVar(inst, 4008) if (HOState == 49) then scr.SetProperty("ledHOffset", "Value", "0") else scr.SetProperty("ledHOffset", "Value", "1") end ------------------------------------------------------- -- Set Spindle Ratio DRO ------------------------------------------------------- local spinmotormax, rangemax, ratio spinmotormax, rc = scr.GetProperty('droSpinMotorMax', 'Value') spinmotormax = tonumber(spinmotormax) or 1 rangemax, rc = scr.GetProperty('droRangeMax', 'Value') rangemax = tonumber(rangemax) or 1 ratio = (rangemax / spinmotormax) scr.SetProperty('droRatio', 'Value', tostring(ratio))
------------------------------------------------------- -- Set Feedback Ratio DRO Updated 5-30-16 ------------------------------------------------------- local range, rc = mc.mcSpindleGetCurrentRange(inst) local fbratio, rc = mc.mcSpindleGetFeedbackRatio(inst, range) scr.SetProperty('droFeedbackRatio', 'Value', tostring(fbratio))
------------------------------------------------------- -- PLC First Run ------------------------------------------------------- if (testcount == 1) then --Set Keyboard input startup state local iReg = mc.mcIoGetHandle (inst, "Keyboard/Enable") mc.mcIoSetState(iReg, 1) --Set register to 1 to ensure KeyboardInputsToggle function will do a disable. KeyboardInputsToggle() DecToFrac(0) DecToFrac(1) DecToFrac(2) scr.SetProperty('toolpath1', 'Hidden', '0') --------------------------------------------------------------- -- Set Persistent DROs. ---------------------------------------------------------------
DROTable = { [1034] = "droEdgeFinder", [1035] = "droGageBlock", [1036] = "droGageBlockT" }
-- ******************************************************************************************* -- -- The following is a loop. As a rule of thumb loops should be avoided in the PLC Script. -- -- However, this loop only runs during the first run of the PLC script so it is acceptable.-- -- ******************************************************************************************* --
for name,number in pairs (DROTable) do -- for each paired name (key) and number (value) in the DRO table local droName = (DROTable[name]) -- make the variable named droName equal the name from the table above --wx.wxMessageBox (droName) local val = mc.mcProfileGetString(inst, "PersistentDROs", (droName), "NotFound") -- Get the Value from the profile ini if(val ~= "NotFound")then -- If the value is not equal to NotFound scr.SetProperty((droName), "Value", val) -- Set the dros value to the value from the profile ini end -- End the If statement end -- End the For loop
end ------------------------------------------------------- --This is the last thing we do. So keep it at the end of the script! machStateOld = machState; machWasEnabled = machEnabled;

if SigLib[sig] ~= nil then
    SigLib[sig](state);
end

if MsgLib ~= nil and MsgLib[msg] ~= nil then
    MsgLib[msg](param1, param2);
end

mcNotebookPage.Event:
local rc;
local tabG_Mdi, rc = scr.GetProperty("nbGCodeMDI1", "Current Tab")

--See if we have to do an MDI command
if (tonumber(tabG_Mdi) == 1 ) then
    scr.SetProperty('btnCycleStart', 'Label', 'Cycle Start\nMDI');
else
    scr.SetProperty('btnCycleStart', 'Label', 'Cycle Start\nGcode');
end

mcButton2.Event:
local inst = mc.mcGetInstance();
local hreg = mc.mcRegGetHandle(inst, "iRegs0/2025/LaserOffsetX") -- scr.GetProperty("droXLaserOffset", "Value") -- -4.7335 -- X axis distance from laser crosshairs to spindle center local offsetX = mc.mcRegGetValueLong(hreg) hreg = mc.mcRegGetHandle(inst, "iRegs0/2025/LaserOffsetY") --scr.GetProperty("droYLaserOffset", "Value") -- -0.0137 -- Y axis distance from laser crosshairs to spindle center local offsetY = mc.mcRegGetValueLong(hreg)
mc.mcAxisSetPos(inst,mc.X_AXIS,offsetX) mc.mcAxisSetPos(inst,mc.Y_AXIS,offsetY) mc.mcCntlSetLastError(inst, "Workpiece X/Y DRO set to laser crosshair X = ".. offsetX .. " Y = " .. offsetY)

mcNotebookPage.Event:
local inst = mc.mcGetInstance()
local hSig = mc.mcSignalGetHandle(inst, mc.OSIG_RUNNING_GCODE);
local sigState = mc.mcSignalGetState(hSig);

if (sigState ~= 1) then 
	scr.EditorLoadCurrent("Edit1")
end

local rc;
local tabG_Mdi, rc = scr.GetProperty("nbGCodeMDI1", "Current Tab")

--See if we have to do an MDI command
if (tonumber(tabG_Mdi) == 1 ) then
    scr.SetProperty('btnCycleStart', 'Label', 'Cycle Start\nMDI');
else
    scr.SetProperty('btnCycleStart', 'Label', 'Cycle Start\nGcode');
end

